mapper.update:
UPDATE board_reply 
SET reply_content = #{replyContent},
	reply_name = #{replyName},
	reply_regdate = #{replyRegDate}
where	recipe_board_no = #{recipeBoardNo}
and	   reply_no=#{replyNo}

