AND   p.product_name = #{productName}
AND	  p.store_id = pp.store_id
AND   p.product_id = pp.product_id


WHERE p.store_id = #{storeId} 
AND   p.product_id = #{productId}
AND	  p.store_id = pp.store_id
AND   p.product_id = pp.product_id


