mapper.select:
SELECT COUNT(*)
FROM   reservation_details
WHERE  store_id = #{value}

SELECT COUNT(*)
FROM   reservation_details
WHERE  user_id = #{value}

choose.when:

SELECT COUNT(reservation_no)
FROM reservation_details
WHERE user_id = #{methodContent}
AND	  store_id = #{storeId}


SELECT COUNT(reservation_no)
FROM reservation_details
WHERE reservation_time = #{methodContent}
AND	  store_id = #{storeId}

choose.otherwise:


SELECT COUNT(reservation_no)
FROM reservation_details
WHERE product_hope_time = #{methodContent}
AND	  store_id = #{storeId}
